function find_all_paths_generalized(graph, start, end, depth,basic_careers=[],path=[]){
    graph_temp=graph;
    graph_temp[start]=graph_temp[start].concat(basic_careers);
    return find_all_paths(graph_temp,start,end,depth,path)
}

 function find_all_paths(graph, start, end, depth,path){
        path = path.concat( [start]);
        if(start == end){
			return [path];}
        if (path.length>depth){
		return[];
		}
        if(!(start in graph)){
		return [];}
        let paths = [];
		
		for(let i=0;i<graph[start].length;i++){
			node=graph[start][i];
			if(path.indexOf(node)==-1){
				tempp=find_all_paths(graph, node, end,depth, path);
				for(let j=0;j<tempp.length;j++){
					paths.push(tempp[j]);	
				}
				
			}
		}
		
        return paths;
}

